import React from 'react';
import { Text } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import HomeScreen from '../screens/HomeScreen';
import NoteDetailScreen from '../screens/NoteDetailScreen';
import CreateNoteScreen from '../screens/CreateNoteScreen';
import ProfileScreen from '../screens/ProfileScreen';
import SettingsScreen from '../screens/SettingsScreen';

const Stack = createNativeStackNavigator();
const Tab = createBottomTabNavigator();

function HomeStack() {
    return (
        <Stack.Navigator>
            <Stack.Screen
                name="NotesList"
                component={HomeScreen}
                options={{ title: 'Notlarƒ±m' }}
            />
            <Stack.Screen
                name="NoteDetail"
                component={NoteDetailScreen}
                options={{ title: 'Not Detayƒ±' }}
            />
            <Stack.Screen
                name="CreateNote"
                component={CreateNoteScreen}
                options={({ route }) => ({
                    title: route.params?.note ? 'Notu D√ºzenle' : 'Yeni Not'
                })}
            />
        </Stack.Navigator>
    );
}

export default function AppNavigator() {
    return (
        <Tab.Navigator
            screenOptions={{
                tabBarActiveTintColor: '#007AFF',
                tabBarInactiveTintColor: 'gray',
                headerShown: false
            }}
        >
            <Tab.Screen
                name="Home"
                component={HomeStack}
                options={{
                    tabBarLabel: 'Notlar',
                    tabBarIcon: ({ color }) => (
                        <Text style={{ color, fontSize: 24 }}>üìù</Text>
                    )
                }}
            />
            <Tab.Screen
                name="Profile"
                component={ProfileScreen}
                options={{
                    tabBarLabel: 'Profil',
                    tabBarIcon: ({ color }) => (
                        <Text style={{ color, fontSize: 24 }}>üë§</Text>
                    )
                }}
            />
            <Tab.Screen
                name="Settings"
                component={SettingsScreen}
                options={{
                    tabBarLabel: 'Ayarlar',
                    tabBarIcon: ({ color }) => (
                        <Text style={{ color, fontSize: 24 }}>‚öôÔ∏è</Text>
                    )
                }}
            />
        </Tab.Navigator>
    );
}
