import axios from 'axios';

const API_URL = 'http://localhost:4000'; // JSON Server adresi

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Kimlik doğrulama işlemleri
export const loginUser = async (credentials) => {
    try {
        const response = await api.get('/user', {
            params: { email: credentials.email }
        });
        const user = response.data;

        if (user && user.password === credentials.password) {
            const { password, ...userData } = user;
            return userData;
        }
        throw new Error('Geçersiz kullanıcı adı veya şifre');
    } catch (error) {
        console.error('Giriş hatası:', error);
        throw error;
    }
};

export const registerUser = async (userData) => {
    try {
        const response = await api.post('/user', {
            ...userData,
            avatar: 'https://via.placeholder.com/150',
            createdAt: new Date().toISOString()
        });
        const { password, ...user } = response.data;
        return user;
    } catch (error) {
        console.error('Kayıt hatası:', error);
        throw error;
    }
};

export const updateUserProfile = async (userId, userData) => {
    try {
        const response = await api.put(`/user/${userId}`, userData);
        return response.data;
    } catch (error) {
        console.error('Profil güncelleme hatası:', error);
        throw error;
    }
};

// Not işlemleri
export const getNotes = async () => {
    try {
        const response = await api.get('/notes');
        return response.data;
    } catch (error) {
        console.error('Notlar alınırken hata:', error);
        throw error;
    }
};

export const getNote = async (id) => {
    try {
        const response = await api.get(`/notes/${id}`);
        return response.data;
    } catch (error) {
        console.error('Not detayı alınırken hata:', error);
        throw error;
    }
};

export const createNote = async (data) => {
    try {
        const noteData = {
            ...data,
            createdAt: new Date().toISOString()
        };
        const response = await api.post('/notes', noteData);
        return response.data;
    } catch (error) {
        console.error('Not oluşturulurken hata:', error);
        throw error;
    }
};

export const updateNote = async (id, data) => {
    try {
        const noteData = {
            ...data,
            updatedAt: new Date().toISOString()
        };
        const response = await api.put(`/notes/${id}`, noteData);
        return response.data;
    } catch (error) {
        console.error('Not güncellenirken hata:', error);
        throw error;
    }
};

export const deleteNote = async (id) => {
    try {
        await api.delete(`/notes/${id}`);
        return true;
    } catch (error) {
        console.error('Not silinirken hata:', error);
        throw error;
    }
};

export default api;
