import axios from 'axios';

const API_URL = 'http://localhost:3000'; // JSON Server adresi

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

export const getNotes = async () => {
    try {
        const response = await api.get('/notes');
        return response.data;
    } catch (error) {
        console.error('Notlar alınırken hata:', error);
        throw error;
    }
};

export const getNote = async (id) => {
    try {
        const response = await api.get(`/notes/${id}`);
        return response.data;
    } catch (error) {
        console.error('Not detayı alınırken hata:', error);
        throw error;
    }
};

export const createNote = async (data) => {
    try {
        const noteData = {
            ...data,
            createdAt: new Date().toISOString()
        };
        const response = await api.post('/notes', noteData);
        return response.data;
    } catch (error) {
        console.error('Not oluşturulurken hata:', error);
        throw error;
    }
};

export const updateNote = async (id, data) => {
    try {
        const noteData = {
            ...data,
            updatedAt: new Date().toISOString()
        };
        const response = await api.put(`/notes/${id}`, noteData);
        return response.data;
    } catch (error) {
        console.error('Not güncellenirken hata:', error);
        throw error;
    }
};

export const deleteNote = async (id) => {
    try {
        await api.delete(`/notes/${id}`);
        return true;
    } catch (error) {
        console.error('Not silinirken hata:', error);
        throw error;
    }
};

export default api;
