import React, { useState, useEffect } from 'react';
import {
    View,
    TextInput,
    StyleSheet,
    TouchableOpacity,
    Text,
    KeyboardAvoidingView,
    Platform,
    Alert,
    ActivityIndicator,
} from 'react-native';
import { createNote, updateNote } from '../services/api';

export default function CreateNoteScreen({ route, navigation }) {
    const editingNote = route.params?.note;
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (editingNote) {
            setTitle(editingNote.title);
            setContent(editingNote.content);
        }
    }, [editingNote]);

    const handleSave = async () => {
        if (!title.trim() || !content.trim()) {
            Alert.alert('Uyarı', 'Lütfen başlık ve içerik alanlarını doldurun.');
            return;
        }

        try {
            setLoading(true);
            if (editingNote) {
                await updateNote(editingNote.id, { title, content });
            } else {
                await createNote({ title, content });
            }
            navigation.goBack();
        } catch (error) {
            console.error('Not kaydedilirken hata oluştu:', error);
            Alert.alert(
                'Hata',
                'Not kaydedilirken bir hata oluştu. Lütfen tekrar deneyin.'
            );
        } finally {
            setLoading(false);
        }
    };

    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
            style={styles.container}
        >
            <View style={styles.form}>
                <TextInput
                    style={styles.titleInput}
                    placeholder="Başlık"
                    value={title}
                    onChangeText={setTitle}
                    editable={!loading}
                />
                <TextInput
                    style={styles.contentInput}
                    placeholder="İçerik"
                    value={content}
                    onChangeText={setContent}
                    multiline
                    textAlignVertical="top"
                    editable={!loading}
                />
                <TouchableOpacity
                    style={[styles.saveButton, loading && styles.saveButtonDisabled]}
                    onPress={handleSave}
                    disabled={loading}
                >
                    {loading ? (
                        <ActivityIndicator color="#fff" />
                    ) : (
                        <Text style={styles.saveButtonText}>Kaydet</Text>
                    )}
                </TouchableOpacity>
            </View>
        </KeyboardAvoidingView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    form: {
        flex: 1,
        padding: 16,
    },
    titleInput: {
        fontSize: 20,
        fontWeight: '500',
        marginBottom: 16,
        padding: 8,
        borderBottomWidth: 1,
        borderBottomColor: '#ddd',
    },
    contentInput: {
        flex: 1,
        fontSize: 16,
        lineHeight: 24,
        padding: 8,
    },
    saveButton: {
        backgroundColor: '#007AFF',
        padding: 16,
        borderRadius: 8,
        alignItems: 'center',
        marginTop: 16,
    },
    saveButtonDisabled: {
        backgroundColor: '#ccc',
    },
    saveButtonText: {
        color: '#fff',
        fontSize: 16,
        fontWeight: '600',
    },
});
